import React, { PureComponent } from 'react';
import { withRouter } from 'react-router-dom';
import styles from './styles.module.scss'
import PropTypes from "prop-types";

class Search extends PureComponent {
  constructor(props) {
    super(props);
    this.inputRef = React.createRef();
  }

  static propTypes = {
    sources: PropTypes.array,
  };

  state = {
    search: null,
    isOpen: false,
  };

  handleSearchChange = (e) => {
    e.persist();
    this.setState({ search: e.target.value })
  };

  requestSearch = () => {
    this.props.history.push(`/search/${this.state.search}`)
  };

  handleKeyPress = (evt) => {
    if(evt.keyCode === 13){
      this.setState({ isOpen: false })
      this.requestSearch()
    }
  };

  handleCollapse = () => {
    if (!this.state.isOpen) {
      this.inputRef.current.setAttribute("autofocus", "");
    }
    this.setState((prevState) => ({
      isOpen: !prevState.isOpen
    }))
  };

  render() {
    const { isOpen } = this.state;
    const { sources } = this.props;
    return (
      <div className={styles.search}>
        <div className={ `${styles['search-wrapper']} ${isOpen ? styles['is-open'] : null }` }>
          <input
            ref={this.inputRef}
            type="text"
            placeholder="Search..."
            onKeyUp={(e) => this.handleKeyPress(e)}
            onChange={(e) => this.handleSearchChange(e)}
          />
          <select name="" id="">
            { sources && sources.map(source => (
              <option value={source.id}>{source.name}</option>)
            )}
          </select>
          <button onClick={this.handleCollapse}><i className="ti-close" /></button>
        </div>
        <button onClick={this.handleCollapse}><i className="ti-search" /></button>
      </div>
    )
  }
}

export default withRouter(Search)
