import React, {PureComponent} from "react";

class ContactForm extends PureComponent {
  state = {
    email: '',
    message: '',
  };

  handleInputChange = (e) => {
    console.log(e.target.value)
  };

  render() {
    return (
      <div>
        <input
          type="text"
          name="email"
          onChange={(e) => this.handleInputChange(e)}
        />
        <textarea
          name="message"
          cols="30"
          rows="10"
          onChange={(e) => this.handleInputChange(e)}
        />
      </div>
    )
  }
}

export default ContactForm
