import React, { PureComponent } from 'react';
import { getEverything } from '../services/client';
import ArticlesList from '../components/Article/ArticlesList';

class Home extends PureComponent {
  loadNews = () => {
    getEverything()
  };

  componentDidMount() {
    this.loadNews()
  }

  render() {
    return (
      <>
        <div className="container">
          <h1>Home Page</h1>
          <ArticlesList />
        </div>
      </>
    )
  }
}

export default Home
