import React, {PureComponent} from "react";

class ContactForm extends PureComponent {
  state = {
    email: '',
    message: '',
  };

  handleInputChange = () => {
    return (evt) => {
      console.log(evt.target.value)
    }
  };

  render() {
    return (
      <div>
        <input
          type="text"
          name="email"
          onChange={this.handleInputChange}
        />
        <textarea
          name="message"
          cols="30"
          rows="10"
          onChange={this.handleInputChange}
        />
      </div>
    )
  }
}

export default ContactForm
