import React, { PureComponent } from 'react';
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import { getSources } from '../../services/client';
import styles from './styles.module.scss'
import logo from '../../assets/img/news.svg'
import ArticleDetail from "../Article/ArticleDetail";
import Modal from "../Modal/Modal";

class Home extends PureComponent {
  state = {
    sources: [],
    search: null,
    openModal: false,
  };

  loadSources = () => {
    getSources().then(({ sources }) => {
      if (!sources) return;
      const sourcesCut = [ ...sources ];
      sourcesCut.length = 3;
      this.setState({ sources: sourcesCut })
    })
  };

  handleSearchChange = (e) => {
    e.persist();
    this.setState({ search: e.target.value })
  };

  handleSearch = () => {
    this.props.history.push(`/search/${this.state.search}`)
  };

  handleKeyPress = (evt) => {
    if(evt.keyCode === 13){
      this.props.history.push(`/search/${this.state.search}`)
    }
  };

  componentDidMount() {
    this.loadSources()
  }

  render() {
    const { history } = this.props;
    return (
      <>
        <header className={styles.header}>
          <div className={`${styles.wrapper} container`}>
            <div className={styles.logo}>
              <Link to='/'>
                <img src={logo} alt="logo"/>
                <h2>News App</h2>
              </Link>
            </div>
            <div className={styles.navigation}>
              <ul>
                { this.state.sources && this.state.sources.map(source => (
                    <li key={source.id} className={history.location.pathname === `/source/${source.id}` ? styles.active : ''}>
                      <Link to={`/source/${source.id}`}>
                        { source.name }
                      </Link>
                    </li>
                  ))
                }
              </ul>
              <button className={styles['contact-btn']}>Contact us</button>
              <div className={styles.search}>
                <input
                  type="text"
                  placeholder="Search..."
                  onKeyUp={(e) => this.handleKeyPress(e)}
                  onChange={(e) => this.handleSearchChange(e)}
                />
                <button onClick={this.handleSearch}><i className="ti-search" /></button>
              </div>
            </div>
          </div>
        </header>
        <Modal
          open={this.state.openModal}
          onClose={this.handleModalClose}
        >
          Hey
        </Modal>
      </>
    )
  }
}

export default withRouter(Home)
