import React, {PureComponent} from "react";
import TextField from './TextField';
import ErrorMessage from './ErrorMessage';
import styles from './style.module.scss';

class ContactForm extends PureComponent {
  constructor(props) {
    super(props);
    this.validate = this.validate.bind(this)
    this.emailFieldRef = React.createRef();
    this.messageFieldRef = React.createRef();
  }

  state = {
    fields: {
      email: '',
      message: '',
    },
    errors: {}
  };

  handleInputChange = (e) => {
    const fields = { ...this.state.fields };
    const key = e.target.name;
    const value = e.target.value;
    fields[key] = value;
    this.setState({ fields })
  };

  handleSendClick = () => {
    // const errors = this.validate();
    // this.setState({ errors });
    // if (Object.keys(errors).length) return;
    // this.handleSuccess()
  };

  validate = () => {
    const errors = {};
    errors.email = this.emailFieldRef.current.validate();
    errors.message = this.messageFieldRef.current.validate();
    this.setState({ errors })
  };

  handleSuccess = () => {

  };

  render() {
    const { email, message } = this.state.fields;
    return (
      <div className={styles['contact-form']}>
        <div className="text-center">
          <h2>Get in touch</h2>
        </div>
        {JSON.stringify(this.state.errors)}
        <TextField
          ref={this.emailFieldRef}
          type="text"
          name="email"
          placeholder="Your Email address"
          value={email}
          onChange={(e) => this.handleInputChange(e)}
        />

        <TextField
          ref={this.messageFieldRef}
          type="textarea"
          name="message"
          placeholder="Your Message"
          value={message}
          onChange={(e) => this.handleInputChange(e)}
        />

        <div className="text-center">
          <button
            className={styles['contact-form__btn']}
            onClick={this.validate}
          >
            Send Message
          </button>
        </div>
      </div>
    )
  }
}

export default ContactForm
